function _to_char_dict(CODE_DICT) {
    let entries = Object.entries(CODE_DICT)
    entries = entries.map(r=>[r[0],String.fromCharCode(r[1])])
    return(Object.fromEntries(entries))
}


const CH_DICT = _to_char_dict(CODE_DICT);


module.exports = {
    CODE_DICT,
    CH_DICT
}


